        private static void ShowStringsAndRuntimeTypes()
        {
            // where are stored the literal strings?
            //
            string literal = "I will be frozen in the NonGC heap that corresponds to which generation?";
            Console.WriteLine(literal);
            Console.ReadKey();
            int generation = GC.GetGeneration(literal);
            if (generation == int.MaxValue)
            {
                Console.WriteLine("-1");
            }
            else
            {
                Console.WriteLine(generation);
            }
            Console.WriteLine();


            // literal string with a length larger than 85000 / 2 end up into the LOH
            // literal string with  (64 KB / 2) < length < (85000 / 2) end up into the SOH (gen0)
            //
            Console.WriteLine($"LargeGlobal size = {LargeGlobal.Length * 2}");  // UTF16 encoding
            Console.ReadKey();
            generation = GC.GetGeneration(LargeGlobal);
            Console.WriteLine($"stored in gen{generation} (= in LOH)");
            Console.WriteLine();

            Console.WriteLine($"LongGlobal size = {LongGlobal.Length * 2}");  // UTF16 encoding
            Console.ReadKey();
            generation = GC.GetGeneration(LongGlobal);
            Console.WriteLine($"stored in gen{generation}");
            Console.WriteLine();

            // any other usage of the NonGC Heap?
            var type1 = LargeGlobal.GetType();
            var type2 = typeof(string);
            Console.WriteLine($"The System.Type are same object in memory?");
            Console.ReadKey();
            Console.Write($"{Type.ReferenceEquals(type1, type2)} and stored in ");
            generation = GC.GetGeneration(type1);
            if (generation == int.MaxValue)
            {
                Console.WriteLine("NonGC Heap");
            }
            else
            {
                Console.WriteLine($"gen{generation}");
            }
            Console.ReadKey();
        }

