#region ClrMD
        static void ListFrozenObjects()
        {
            int pid = Process.GetCurrentProcess().Id;
            using (DataTarget target = DataTarget.AttachToProcess(pid, false))
            {
                foreach (ClrInfo clrInfo in target.ClrVersions)
                {
                    ClrRuntime runtime = clrInfo.CreateRuntime();
                    ClrHeap heap = runtime.Heap;
                    if (!heap.CanWalkHeap)
                    {
                        Console.WriteLine("Cannot walk heap...");
                        return;
                    }

                    foreach (ClrSubHeap subHeap in heap.SubHeaps)
                    {
                        Console.Write($"#{subHeap.Index} heap");
                        if (subHeap.HasRegions)
                        {
                            Console.Write(" with regions");
                        }
                        Console.WriteLine();

                        foreach (var segment in subHeap.Segments.OrderBy(s => s.Start))
                        {
                            if (segment.Kind == GCSegmentKind.Frozen)
                            {
                                Console.WriteLine($"NGC | {segment.Start,11:x} - {segment.End,11:x} ({segment.Length})");
                                DumpSegment(segment);
                            }
                        }

                        Console.WriteLine();
                    }
                }
            }

        }

        private static void DumpSegment(ClrSegment segment)
        {
            foreach (var obj in segment.EnumerateObjects())
            {
                Console.Write($"      {obj.Address,12:x} ");
                DumpType(obj);
                Console.WriteLine();
            }
        }

        private static void DumpType(ClrObject obj)
        {
            if (!obj.IsValid)
            {
                Console.Write("<invalid>");
                return;
            }

            if (obj.IsNull)
            {
                Console.Write("null");
            }
            else if (obj.IsFree)
            {
                Console.Write("free");
            }
            else if (obj.IsArray)
            {
                var arr = obj.AsArray();
                Console.Write($"{arr.Type.ComponentType}[{arr.Rank}, {arr.Length}]");
            }
            else
            {
                Console.Write(obj.Type.Name);
                if (obj.Type.IsString)
                {
                    var str = obj.AsString();
                    if (str.Length > 100)
                    {
                        str = str.Substring(0, 100) + "___";
                    }

                    Console.Write($" \"{str}\"");
                }
            }
        }

        private static void DumpLiteralStrings()
        {
            string filename = Assembly.GetCallingAssembly().Location;

            // .NET Core PEReader
            using (var stream = File.OpenRead(filename))
            {
                var peReader = new PEReader(stream);
                var headers = peReader.PEHeaders;
                var peHeader = headers.PEHeader;
                if (peHeader == null)
                {
                    Console.WriteLine($"'{filename}' is not a PE file...");
                    return;
                }

                if (headers.CorHeaderStartOffset == -1)
                {
                    Console.WriteLine($"'{filename}' is not a .NET file...");
                    return;
                }

                var sectionHeaders = headers.SectionHeaders;
                Console.WriteLine($"{sectionHeaders.Length} section headers");
                foreach (var header in sectionHeaders)
                {
                    Console.WriteLine($"{header.Name,9} 0x{header.VirtualAddress:x} ({header.SizeOfRawData})| {header.SectionCharacteristics}");
                }
                Console.WriteLine();

                var corHeader = headers.CorHeader;
                Console.WriteLine($"COR Header {corHeader.MajorRuntimeVersion}.{corHeader.MinorRuntimeVersion}");
                Console.WriteLine($"  COR header : 0x{headers.CorHeaderStartOffset:x} ({corHeader.MetadataDirectory.Size})");

                var metadataReader = peReader.GetMetadataReader();
                var handle = MetadataTokens.UserStringHandle(0);
                do
                {
                    var literal = metadataReader.GetUserString(handle);
                    if (literal.Length > 100)
                    {
                        literal = literal.Substring(0, 100) + "___";
                    }

                    Console.WriteLine($"  '{literal}'");
                    handle = metadataReader.GetNextHandle(handle);
                }
                while (!handle.IsNil);
            }
        }
#endregion

